import React, { useState } from "react";
import Button from "./components/Button";
import Decrement from "./components/Decrement";
import Reset from "./components/Reset.jsx";

import "./styles.css";

function App({ initialCount }) {
  const [count, setCount] = useState((initialCount = 0));

  const incrementCount = increment => {
    setCount(count + increment);
  };

  const decrementCount = decrement => {
    setCount(count - decrement);
  };

  return (
    <div className="App">
      <h1>My React Hooks Counter</h1>
      <Button
        className="btn btn-primary btn-sm m2"
        increment={1}
        onClickFunction={incrementCount}
      />
      <Decrement decrement={1} onClickFunction={decrementCount} />
      <Button
        className="btn btn-primary btn-sm m2"
        increment={10}
        onClickFunction={incrementCount}
      />
      <Decrement
        className="btn btn-primary btn-sm m2"
        decrement={10}
        onClickFunction={decrementCount}
      />
      <Button
        className="btn btn-primary btn-sm m2"
        increment={100}
        onClickFunction={incrementCount}
      />
      <Decrement
        className="btn btn-primary btn-sm m2"
        decrement={100}
        onClickFunction={decrementCount}
      />
      <Button
        className="btn btn-primary btn-sm m2"
        increment={1000}
        onClickFunction={incrementCount}
      />
      <Decrement
        className="btn btn-primary btn-sm m2"
        decrement={1000}
        onClickFunction={decrementCount}
      />
      <br />
      <Reset
        className="btn btn-primary btn-sm m2"
        onClickFunction={() => setCount(initialCount)}
      />
      <br />
      <span>{count}</span>
    </div>
  );
}

export default App;
