import React from "react";

const Button = ({ increment, onClickFunction }) => {
  const handleClick = () => {
    onClickFunction(increment);
  };
  return (
    <button className="btn btn-primary btn-sm m2" onClick={handleClick}>
      +{increment}
    </button>
  );
};

export default Button;
